import "./Expenses.css";
import Card from "./Card";
import ExpensesFilter from "../ExpensesFilter/ExpensesFilter";
import ExpenseItems from "./ExpenseItems";
import { useState } from "react";

function Expenses(props) {
  console.log("in expenses");
  const onYearSelectedHandler = (event) => {
    console.log("Checking expenses for the year: " + event.target.value);
    props.onFilter(event.target.value);

    // setExpensesList(() => {
    //     return props.expenses.filter((expense) => 
    //         new Date(expense.date).getFullYear() === new Date(event.target.value).getFullYear());
    // });
  };

  return (
    <div>
      <ExpensesFilter onYearSelected={onYearSelectedHandler} />
      <Card className="expenses">
        <ExpenseItems expenses={props.expenses} />
      </Card>
    </div>
  );
}

export default Expenses;
