import "./Expenses.css";
import Card from "./Card";
import ExpensesFilter from "../ExpensesFilter/ExpensesFilter";
import ExpenseItems from "./ExpenseItems";
import { useState } from "react";

function Expenses(props) {
    const [expenses, setExpensesList] = useState(props.expenses);

    const onYearSelectedHandler = (event) => {
        console.log(event.target.value);

        setExpensesList(props.expenses.filter((expense) => {
            console.log(expense.date.getFullYear());

            return expense.date.getFullYear() === event.target.value;
        }));
    }

  return (
    <div>
      <ExpensesFilter onYearSelected={onYearSelectedHandler}/>
      <Card className="expenses">
        <ExpenseItems expenses={expenses}/>
      </Card>
    </div>
  );
}

export default Expenses;
