import "./Expenses.css";
import Card from "./Card";
import ExpensesFilter from "../ExpensesFilter/ExpensesFilter";
import ExpenseItems from "./ExpenseItems";
import { useState } from "react";

function Expenses(props) {
//   const [expenses, setExpensesList] = useState(props.expenses);

  const onYearSelectedHandler = (event) => {
    console.log("Checking expenses for the year: " + event.target.value);
    const filteredList = props.expenses.filter(
      (expense) =>
        expense.date.getFullYear() ===
        new Date(event.target.value).getFullYear()
    );
    if(filteredList.length > 0)
        console.log('Found : ' + filteredList.length + ' matches')
    else
        console.log('Found none')
    setExpensesList(filteredList);
  };

  return (
    <div>
      <ExpensesFilter onYearSelected={onYearSelectedHandler} />
      <Card className="expenses">
        <ExpenseItems expenses={props.expenses} />
      </Card>
    </div>
  );
}

export default Expenses;
