import { useState } from "react";
import Expenses from "./components/Expenses";
import NewExpense from "./NewExpense/NewExpense";

const DUMMY_EXPENSES = [
  { id: 'e1', title: 'Toilet Paper', amount: 94.12, date: new Date(2020, 7, 14) },
  { id: 'e2', title: 'New TV', amount: 799.49, date: new Date(2021, 2, 12) },
  { id: 'e3', title: 'Car Insurance', amount: 294.67, date: new Date(2021, 2, 28) },
  { id: 'e4', title: 'New Desk (Wooden)', amount: 450, date: new Date(2021, 5, 12) }
];

function App() {
  const [expenses, setExpenses] = useState(DUMMY_EXPENSES);

  const addExpenseHandler = expense => {
    console.log('New expense added');
    console.log(expense);
    setExpenses((previousExpenses) => {
      return [expense, ...previousExpenses];
    })
  }

  const filterExpensesHandler = selectedfilter => {
    setExpenses(expenses.filter((expense) => 
            new Date(expense.date).getFullYear() === new Date(selectedfilter).getFullYear()));
  }

  return (
    <div>
      <NewExpense onAddExpense={addExpenseHandler}/> 
      <Expenses expenses={expenses} onFilter={filterExpensesHandler}/>
    </div>
  );
}

export default App;
